      out.write("  function passwordCheck(t, passwordId) {\r\n");
      out.write("    // 密码正则检查\r\n");
      out.write("    if (!passwordReg1.test($(t).val())) {\r\n");
      out.write("      // 密码长度在6到18位之间。\r\n");
      out.write("      passwordError = false;\r\n");
      out.write("      $(t).attr(\"data-parsley-password-message\", passwordMsg1);\r\n");
      out.write("      $(t).parsley().validate();\r\n");
      out.write("    } else if (passwordReg2.test($(t).val())) {\r\n");
      out.write("      // 密码不能全是数字。\r\n");
      out.write("      passwordError = false;\r\n");
      out.write("      $(t).attr(\"data-parsley-password-message\", passwordMsg2);\r\n");
      out.write("      $(t).parsley().validate();\r\n");
      out.write("    } else if (passwordReg3.test($(t).val())) {\r\n");
      out.write("      // 密码不能全是字母。\r\n");
      out.write("      passwordError = false;\r\n");
      out.write("      $(t).attr(\"data-parsley-password-message\", passwordMsg3);\r\n");
      out.write("      $(t).parsley().validate();\r\n");
      out.write("    } else if (!passwordReg4.test($(t).val())) {\r\n");
      out.write("      // 允许含有的特殊字符\r\n");
      out.write("      passwordError = false;\r\n");
      out.write("      $(t).attr(\"data-parsley-password-message\", passwordMsg4);\r\n");
      out.write("      $(t).parsley().validate();\r\n");
      out.write("    } else {\r\n");
      out.write("      passwordError = true;\r\n");
      out.write("    }\r\n");
      out.write("\r\n");
      out.write("    // 密码一致性检查\r\n");
      out.write("    if (passwordError && passwordId) {\r\n");
      out.write("      var $other = $(\"#\" + passwordId);\r\n");
      out.write("      if ($other.val() != \"\") {\r\n");
      out.write("        if ($(t).val() == $other.val()) {\r\n");
      out.write("          passwordError = true;\r\n");
      out.write("          $other.parsley().validate();\r\n");
      out.write("        } else {\r\n");
      out.write("          passwordError = false;\r\n");
      out.write("          $(t).attr(\"data-parsley-password-message\", passwordMsg0);\r\n");
      out.write("        }\r\n");
      out.write("      }\r\n");
      out.write("    }\r\n");
      out.write("\r\n");
